import axios from 'axios';
import { useNavigate, useParams } from "react-router-dom";
import { Cabecera } from "../cabecera/cabecera";
import React, { useState, useEffect } from 'react';


export const AreaPersonal = () => {
    const { idUsuario } = useParams()
    const URIrecetas = "http://localhost:8000/recetas/"
    const URIrecetasUsuario="http://localhost:8000/recetasusuarios/"
    const [recipes, setRecipes] = useState([]);
    const [recetasU, setRecetasU]= useState([])

    useEffect(() => {
        getRecetaByID()
        setRecipesFromBDD()
    }, [])

    const getRecetaByID = async () => {

        const res = await axios.get(URIrecetasUsuario + idUsuario)
        console.log(res)
        setRecetasU(res.data)
    }

    const setRecipesFromBDD = async() => {
        let agregar = []
        const res = {}
        recetasU.map(async (id)=>{
            res = await axios.get(URIrecetas + id)
            console.log(res.data)
            agregar.push(res.data)
        })


    }
    

    return (
        <div>
            <Cabecera />

            <div className="container">
                <div className="row">
                    <div className="col-sm-8 mb-4">
                        <div className="row">

                            {recetasU.map((recipe) => 
                                <div className="col-sm-4" key={recipe.idReceta}>
                                    <div className="card">
                                        <div className="card-body">
                                            <h5 className="card-title">{recipe.idReceta}</h5>
                                        </div>
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
